package c.teamtreehouse.foodieappcardview;public class MealItem {    private String title;    private String info;    private int imageResource;    private String recipeDetails;    public MealItem(String title, String info ){        this.title = title;        this.info = info;}    public void setDesc(String desc){        this.recipeDetails= desc;        }    public   String getDesc(){return recipeDetails;}    public MealItem(String title, String info, int imgRes){        this.title = title;        this.info = info;        imageResource = imgRes;        }    public String getTitle(){        return title;    }    public String getInfo(){        return info;    }    public int getImageId(){        return imageResource;    }    public String getRecipeDetails(){        return recipeDetails;    }}